async function getUserRepos(href, header = {}) {
    try {
        const response = await fetch(`${href}`, {
            headers: header
        })
        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`)
        }
        return await response.json()
    } catch (error) {
        console.error('Fetch error:', error)
        throw error
    }
}
