const generateList = (links) => {
    // 判断数据类型
    const isArray = Array.isArray(links)
    const linkEntries = isArray ? links : Object.entries(links)

    return `
        <ul style="list-style: none; padding: 0; margin: 0; text-align: center;">
            ${linkEntries
            .map((item) => {
                // 根据数据类型提取 url 和 quality
                const url = isArray ? item.url : item[1]
                const quality = isArray ? item.quality : item[0]

                return `
                        <li style="margin: 10px 0;">
                            <a href="${url}" target="_blank" style="
                                text-decoration: none;
                                color: #007BFF;
                                font-weight: bold;
                                transition: color 0.3s ease;
                            " onmouseover="this.style.color='#0056b3'" onmouseout="this.style.color='#007BFF'">${quality}</a>
                        </li>
                    `
            })
            .join('')}
        </ul>
    `
}

const createPopup = (links) => {
    const popup = document.createElement('div')
    popup.innerHTML = `
        <div style="
            position: fixed;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            padding: 20px;
            background-color: #fff;
            color: #333;
            border-radius: 12px;
            box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
            z-index: 1000;
            max-width: 90%;
            width: 100%;
            max-height: 80%;
            overflow-y: auto;
            font-family: 'Arial', sans-serif;
        ">
            <button style="
                position: absolute;
                top: 8px;
                right: 8px;
                background-color: #e74c3c;
                color: #fff;
                border: none;
                border-radius: 50%;
                width: 24px;
                height: 24px;
                cursor: pointer;
                font-size: 14px;
                display: flex;
                align-items: center;
                justify-content: center;
                line-height: 1;
            " onclick="this.parentElement.remove()">×</button>
            ${generateList(links)}
        </div>
    `
    document.body.appendChild(popup)
}
function isMobileDevice() {
    return /Mobi|Android|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent)
}
