const directDownload = true
const infiniteLool = true





const loopVideo = () => {
    const video = document.querySelector('video')
    if (video && !video.loop) {
        video.loop = true
    }
}
const ThemeProgressbar = () => {
    const css_248z = '.html5-play-progress,.ytp-play-progress{background:url("data:image/png;base64,") repeat-x!important;background:linear-gradient(180deg,red 0,red 16.5%,#f90 0,#f90 33%,#ff0 0,#ff0 50%,#3f0 0,#3f0 66%,#09f 0,#09f 83.5%,#63f 0,#63f)!important;background:-webkit-linear-gradient(top,red,red 16.5%,#f90 0,#f90 33%,#ff0 0,#ff0 50%,#3f0 0,#3f0 66%,#09f 0,#09f 83.5%,#63f 0,#63f)!important;background:-moz-linear-gradient(top,red 0,red 16.5%,#f90 16.5%,#f90 33%,#ff0 33%,#ff0 50%,#3f0 50%,#3f0 66%,#09f 66%,#09f 83.5%,#63f 83.5%,#63f 100%)!important}.html5-load-progress,.ytp-load-progress{background:url("data:image/gif;base64,")!important}.html5-scrubber-button,.ytp-scrubber-button{background:url("data:image/gif;base64,")!important;border:none!important;height:21px!important;margin-left:-18px!important;margin-top:0!important;transform:scale(.8);-webkit-transform:scale(.8);-moz-transform:scale(.8);-ms-transform:scale(.8);width:34px!important}.ytp-progress-bar-container:hover .ytp-load-progress,.ytp-progress-bar-container:hover .ytp-scrubber-button{image-rendering:pixelated}.html5-progress-bar-container,.ytp-progress-bar-container{height:12px!important}.html5-progress-bar,.ytp-progress-bar{margin-top:12px!important}.html5-progress-list,.video-ads .html5-progress-list.html5-ad-progress-list,.video-ads .ytp-progress-list.ytp-ad-progress-list,.ytp-progress-list{height:12px!important}.ytp-volume-slider-track{background:#0c4177!important}'
    GM_addStyle(css_248z)
}

let escapeHTMLPolicy = 'trustedTypes' in window
    ? trustedTypes.createPolicy('forceInner', { createHTML: html => html })
    : { createHTML: html => html }
function screenBtnUpdate() {
    let $miniplayerBtn = document.querySelector('button.ytp-miniplayer-button')
    if ($miniplayerBtn && !document.getElementById('ytp-screenshot-button')) {
        const $btn = document.createElement('button')
        $btn.id = 'ytp-screenshot-button'
        $btn.classList.add('ytp-screenshot-button', 'ytp-button')
        $btn.dataset.priority = '5'
        $btn.dataset.tooltipTargetId = 'ytp-screenshot-button'
        $btn.dataset.titleNoTooltip = 'Screenshot'
        $btn.ariaLabel = 'Screenshot'
        $btn.title = 'Screenshot'
        $btn.innerHTML = escapeHTMLPolicy.createHTML(`<svg height="100%" version="1.1" viewBox="-300 -1260 1560 1560" width="100%">
            <use class="ytp-svg-shadow" xlink:href="#ytp-id-screenshot-svg"></use>
            <path
                d="M200-120q-33 0-56.5-23.5T120-200v-560q0-33 23.5-56.5T200-840h560q33 0 56.5 23.5T840-760v560q0 33-23.5 56.5T760-120H200Zm0-80h560v-560H200v560Zm40-80h480L570-480 450-320l-90-120-120 160Zm-40 80v-560 560Z"
                fill="#fff" id="ytp-id-screenshot-svg"></path>
        </svg>`)
        $btn.addEventListener('click', screenshot)

        insertBefore($btn, $miniplayerBtn)
    }

    requestAnimationFrame(screenBtnUpdate)
}
function insertBefore($element, $sibling) {
    $sibling.parentElement.insertBefore($element, $sibling)
}
function screenshot() {

    const $video = document.querySelector('#player video')
    if (!$video) {
        console.error('No video found to screenshot!')
        return
    }

    let wasPlaying = !$video.paused
    if (wasPlaying) $video.pause()

    const $canvas = document.createElement('canvas')
    $canvas.width = $video.videoWidth
    $canvas.height = $video.videoHeight

    let oldCss = $video.style.cssText
    $video.style.width = $video.videoWidth + 'px'
    $video.style.height = $video.videoHeight + 'px'

    const ctx = $canvas.getContext('2d')
    ctx.drawImage($video, 0, 0, $video.videoWidth, $video.videoHeight)

    $canvas.toBlob(blob => {

        if (directDownload) {
            const a = document.createElement('a')
            a.href = URL.createObjectURL(blob)
            a.download = `${getFileName()}.png`
            a.click()
        } else {
            const item = new ClipboardItem({ 'image/png': blob })
            navigator.clipboard.write([item])
        }

        $video.style.cssText = oldCss
        $canvas.remove()
        if (wasPlaying) $video.play()

    })

}
function getFileName() {
    const safeFileName = document.title.replace(/[\\/:*?"<>|]/g, '').replace(' - YouTube', '')
    return safeFileName
}
if (infiniteLool) {
    const observer = new MutationObserver(loopVideo)
    observer.observe(document.body, { childList: true, subtree: true })
}

requestAnimationFrame(screenBtnUpdate)
ThemeProgressbar()
